mapper.select:
select employee_id, first_name, hire_date, salary, department_id
from temp
where employee_id = #{eid}

mapper.update:
update temp
set first_name = #{first_name}
    ,hire_date = #{hire_date}
    ,salary = #{salary}
    ,department_id = #{department_id}
where employee_id = #{employee_id}
